import React, {Component} from 'react';
import { Rate } from 'antd';
import {Link} from "react-router-dom";
class Goodslist extends Component {
    componentDidMount(){
        window.addEventListener("scroll",this.loadmore)
    }
    loadmore=()=>{
        //1\获取最底端的元素
        const  ele=this.refs.more;
        // 2、监听当他的top小于视口的高度时候
        const top=ele.getBoundingClientRect().top;
        //3\获取视口的高度
        const windowH=window.screen.height;
        //4\判断：
        if(top<windowH){
            this.props.addmore()
        }
    }
    render() {
        return (
            <div className="goodslist ">

            {/*6.3商品详情*/}
                <Link to="/food/shopDetails">
                    <ul>
                        {/*11111111111111111111111111111111*/}
                        {
                            this.props.list.length>0&&this.props.list.map((item,index)=>{
                                return(
                                    <li key={index}>
                                        <div className="left">
                                            <img src={item.img} alt={item.title}/>
                                        </div>
                                        {/*未完待续***************************************************/}
                                        <div className="right">
                                            {/*6.3.2.1标题*/}
                                            <div className="title2">
                                                <h2>{item.title}</h2>
                                                <span>...</span>
                                            </div>
                                            {/*6.3.2.2平分*/}
                                            <div className="mark">
                                                <Rate value={4}></Rate>
                                                <span>{item.ratingScore}</span>
                                                <span>月销{item.monthSaleCount}单</span>
                                            </div>
                                            {/*6.3.2.3起送*/}
                                            <div className="startsprice">
                                                <div className="price">
                                                    <span>￥{item.startPrice}起送</span>
                                                    <span>远距离配送费￥{item.freightPrice}</span>
                                                </div>
                                                <div className="meter">
                                                    <span>
                                                        {/*处理距离*/}
                                                        {
                                                            item.range>1000?item.range/1000+"km":item.range+"m"
                                                        }
                                                    </span>
                                                    <span>{item.time}分钟</span>
                                                </div>


                                            </div>
                                            {/*6.3.2.4店铺tese*/}
                                            <div className="feature">
                                                <span>包子粥店</span>
                                                <span>品牌联盟</span>

                                            </div>
                                            {/*优惠*/}
                                            <div className="discounts">
                                                <div className="ff">
                                                    <div className="first">

                                                        {item.promotion.map((item,index)=>{
                                                            return (
                                                                <div className="shou" key={index}>
                                                                    <div>{item.type.name}</div>
                                                                    <div>{item.detail}</div>
                                                                </div>
                                                            )
                                                        })}

                                                    </div>
                                                </div>
                                                {/*应该单独成立为一个组件*/}

                                                <div className="active">
                                                    17个活动<i className="iconfont icon-plus-select-down"></i>
                                                </div>


                                            </div>

                                        </div>
                                    </li>
                                )
                            })
                        }


                    </ul>
                    <div className="addmore" ref="more">加载更多。。。</div>
                </Link>


            </div>
        );
    }
}

export default Goodslist;
