import React, {Component} from 'react';
import {NavLink} from "react-router-dom";
import './listmsg.scss';
import '../../recommend/recommend.scss';
import Goodslist from "../../recommend/goodslist";

// import React, {Component} from 'react';
// import "./recommend.scss";
//
// import Recommendtitle from "./recommendtitle";
// import Recommendlist from "./recommendlist";
// import Goodslist from "./goodslist";

//引入样式
import '../../../publicstyle/publicstyle.css';
import Recommendlist from "../../recommend/recommendlist";
import Foodtit from "./foodtit";
import Axios from "axios/index";
class Productlistmsg extends Component {
    state={
        tit:['美食','甜品饮品','商超便利','预定早餐','果蔬生鲜','新店特惠','准时达','晚餐','汉堡薯条','包子粥店','鲜花蛋糕','麻辣烫','川香菜','披萨意面','异国料理']
   ,
        id:"",
        list:[]
    }
    //获取路由，根据路由的id加载不同的值
    componentDidMount(){
        console.log(this.props.match.params.id);
        //1、获取路由中的动态参数：为了渲染标题
        this.setState({
            id:this.props.match.params.id
        })
        Axios.get("http://47.100.98.54:9019/api/category/list:id").then((res)=>{
            this.setState({
                list:res.data.data
            })
        })
    }

    addmorefn=()=>{
        Axios.get("http://47.100.98.54:9019/api/category/list:id").then((res)=>{
            this.setState({
                //将每一个获取的数据拼在一起
                list:this.state.list.concat(res.data.data)
            })
        })
        console.log(333)
    }

    render() {
        return (
            <div className="productlistmsg">
                {/*1标题区域*/}
                <Foodtit tit={this.state.tit} index={this.state.id}/>

                {/*2、食物分类列表去*/}
                <div className="classification">
                    <div className="foodListBox">
                        <ul>
                            <li>
                                <NavLink exact to='/productlist/list1'  activeClassName="active" >全部</NavLink>
                            </li>
                            <li>
                                <NavLink  to='/productlist/list2' activeClassName="active"   >简餐便当</NavLink>
                            </li>
                            <li>
                                <NavLink to='/productlist/list3' activeClassName="active" >小吃炸串</NavLink>
                            </li>
                            <li>
                                <NavLink to='/productlist/list4' activeClassName="active" >地方菜系</NavLink>
                            </li>
                            <li>
                                <NavLink to='/productlist/list5' activeClassName="active" >面食粥点</NavLink>
                            </li>
                            <li>
                                <NavLink to='/productlist/list6' activeClassName="active" >汉堡披萨</NavLink>
                            </li>
                        </ul>

                    </div>
                    <div className="foodDown iconfont icon-xiala2"></div>

                </div>
                {/*3具体分类的食物列表区域*/}
                <Recommendlist/>
                <Goodslist list={this.state.list} addmore={this.addmorefn}/>
            </div>

        );
    }
}

export default Productlistmsg;
