import React, {Component} from 'react';
import './activity.scss';
//引入组件
import { Carousel} from 'antd';
import {Link} from "react-router-dom";
//引入样式
import '../../publicstyle/publicstyle.css';
import Axios from "axios/index";
class Activity extends Component {

    state={
        activity:[]
    }
    componentDidMount(){
        //获取抢购的数据
        Axios.get('http://47.100.98.54:9019/api/promotion').then((res)=>{
            // console.log(res.data.data)
            this.setState({
                activity: res.data.data
            })
            console.log(this.state. activity)
        })
    }
    render() {
        return (
            <div className="activity mt01rem plr04rem">


                <Carousel autoplay>
                    {
                        this.state.activity.map((item,index)=>{
                            return(
                                <Link to={"api/promotionSub:"+item.id} key={index}>
                                    <img src={item.img} alt={item.id}/>
                                </Link>
                            )

                        })
                    }

                    {/*<Link to="/activity/flower2">*/}
                        {/*<img src={require("../../static/img/activity/02.webp")} alt="1"/>*/}
                    {/*</Link>*/}
                    {/*<Link to="/activity/flower3">*/}
                        {/*<img src={require("../../static/img/activity/03.webp")} alt="1"/>*/}
                    {/*</Link>*/}
                    {/*<Link to="/activity/flower4">*/}
                        {/*<img src={require("../../static/img/activity/04.webp")} alt="1"/>*/}
                    {/*</Link>*/}

                </Carousel>,

            </div>
        );
    }
}

export default Activity;
