import React, {Component} from 'react';
import "./recommend.scss";

import Recommendtitle from "./recommendtitle";
import Recommendlist from "./recommendlist";
import Goodslist from "./goodslist";
import Axios from "axios";

//引入样式
import '../../publicstyle/publicstyle.css';
class Recommend  extends Component {
    state={
        list:[]
    }
    addmorefn=()=>{
        // Axios.get("http://47.100.98.54:9019/api/category/list:id").then((res)=>{
        //     this.setState({
        //         //将每一个获取的数据拼在一起
        //         list:this.state.list.concat(res.data.data)
        //     })
        // })
        console.log(333)
    }
    componentDidMount(){
        Axios.get("http://47.100.98.54:9019/api/category/list:id").then((res)=>{
            this.setState({
                list:res.data.data
            })
        })
    }

    render() {
        console.log(this.addmorefn);
        return (
            <div className="recommend ">
                {/*6.1推荐标题*/}

                <Recommendtitle/>
                {/*6.2推荐列表*/}
                <Recommendlist/>

                {/*6.3商品详情*/}
                <Goodslist list={this.state.list} addmore={this.addmorefn}/>



            </div>
        );
    }
}

export default Recommend ;
